import React, {Component} from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';

export default class News extends Component {


    constructor(){
        super();
        console.log("constructor of news component");
        /*
        - articles: articles will be fetched from the newsapi.org API
        - loading: default false, used for loader
        - page: page indicates page number or used for pagination
        */
        this.state = {
            articles: this.articles,
            loading: false,
            page: 1,
            totalResults: this.totalResults
        }
    }

    //Lifecycle method
    async componentDidMount(){
      console.log("promise")
      let url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=55cc10bd91e247e0a38d9716ec2ebfe6&pageSize=${this.props.pageSize}`;
      this.setState({loading: true});
      let response = await fetch(url, {
        method: 'GET',
        headers: {
          Accept: 'application/json',
        },
      });

      let parsedData = await response.json()
      this.setState({
        articles: parsedData.articles, 
        totalResults: parsedData.totalResults,
        loading: false
      })
    }

    //Handler to handle previous page button
    handlePreviousPage = async () => {
      console.log("previous");
      let updatePageNumber = this.state.page - 1

      this.setState({loading: true});
      let parsedData = await this.fetchTopHeadlines(updatePageNumber);
      this.setState ({
        articles: parsedData.articles,
        page: updatePageNumber,
        loading: false
      })
    }

    //Handler to handle Next page button
    handleNextPage = async () => {
      console.log("Next");
      let updatePageNumber = this.state.page + 1

      this.setState({loading: true});
      if( updatePageNumber <= Math.ceil(this.state.totalResults/this.props.pageSize) ){
        let parsedData = await this.fetchTopHeadlines(updatePageNumber);
        this.setState ({
          articles: parsedData.articles,
          page: updatePageNumber,
          loading: false
        })
      }
    }

    //This method will fetch Top Headlines and returns the result as JSON response
    fetchTopHeadlines = async (updatePageNumber) => {
      //Need to move URL to constants/url file
      let url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=55cc10bd91e247e0a38d9716ec2ebfe6&page=${updatePageNumber}&pageSize=${this.props.pageSize}`;
      let response = await fetch(url, {
        method: 'GET',
        headers: {
          Accept: 'application/json',
        },
      });
      return await response.json()
    }

    render(){
        return(
            <div className='container my-3'>
                <h1 className='text-center'>Top Headlines</h1>
                {this.state.loading && <Spinner />}
                <div className='row'>
                {this.state.articles?.map((element)=>{
                    return <div className="col-md-4" key={element.url}>
                    <NewsItem title={element.title?element.title.slice(0, 45):""} description={element.description?element.description.slice(0, 88):""} 
                    imageUrl={element.urlToImage?element.urlToImage:"%PUBLIC_URL%/news-api.png"} newsUrl={element.url}/>
                </div>
                })}
                </div>
                <div className='container d-flex justify-content-between'>
                <button type="button" className="btn btn-secondary btn-lg" onClick={this.handlePreviousPage} disabled={this.state.page <= 1}>&larr; Previous</button>
                <button type="button" className="btn btn-secondary btn-lg" onClick={this.handleNextPage} 
                disabled={this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pageSize)}>Next &rarr;</button>
                </div>

            </div>
        )
    }

}
