import React, {Component} from 'react';
import NewsItem from './NewsItem';

export default class News extends Component {


    constructor(){
        super();
        console.log("constructor of news component");
        this.state = {
            articles: this.articles,
            loading: false,
            page: 1
        }
    }

    async componentDidMount(){
      console.log("promise")
      let url = 'https://newsapi.org/v2/top-headlines?country=us&apiKey=55cc10bd91e247e0a38d9716ec2ebfe6';
      let response = await fetch(url, {
        method: 'GET',
        headers: {
          Accept: 'application/json',
        },
      });

      let parsedData = await response.json()
      this.setState({articles: parsedData.articles})
    }

    handlePreviousPage = () => {
      console.log("previous");
    }

    handleNextPage = () => {
      console.log("Next");
    }

    render(){
        return(
            <div className='container my-3'>
                <h2>Top Headlines</h2>
                <div className='row'>
                {this.state.articles?.map((element)=>{
                    return <div className="col-md-4" key={element.url}>
                    <NewsItem title={element.title?element.title.slice(0, 45):""} description={element.description?element.description.slice(0, 88):""} 
                    imageUrl={element.urlToImage} newsUrl={element.url}/>
                </div>
                })}
                </div>
                <div className='container d-flex justify-content-between'>
                <button type="button" class="btn btn-secondary btn-lg" onClick={this.handlePreviousPage} disabled={this.state.page <= 1}>&larr; Previous</button>
                <button type="button" class="btn btn-secondary btn-lg" onClick={this.handleNextPage}>Next &rarr;</button>
                </div>

            </div>
        )
    }

}
